const toggleTheme=document.getElementById("toggle-color-scheme");const toggleThemeMobile=document.getElementById("toggle-color-scheme__mobile");const resetToSystemTheme=document.getElementById("reset-to-system-color-scheme");const resetToSystemThemeMobile=document.getElementById("reset-to-system-color-scheme__mobile");const moonIcon=document.getElementById("my-moon-icon");const moonIconMobile=document.getElementById("my-moon-icon__mobile");const sunIcon=document.getElementById("my-sun-icon");const sunIconMobile=document.getElementById("my-sun-icon__mobile");const heroForegroundImage=document.getElementById("hero-fr-img");async function setTheme(theme){document.documentElement.setAttribute('data-theme',theme);document.documentElement.setAttribute('data-bs-theme',theme);await new Promise(resolve=>setTimeout(resolve,100));if(theme==="light"){moonIcon.style.opacity=0;moonIconMobile.style.opacity=0;sunIcon.style.opacity=1;sunIconMobile.style.opacity=1;if(heroForegroundImage!=null){heroForegroundImage.style.opacity=0.3;}}else{moonIcon.style.opacity=1;moonIconMobile.style.opacity=1;sunIcon.style.opacity=0;sunIconMobile.style.opacity=0;if(heroForegroundImage!=null){heroForegroundImage.style.opacity=0.1;}}}
function setSystemTheme(){const systemTheme=window.matchMedia("(prefers-color-scheme: dark)").matches?"dark":"light";setTheme(systemTheme);}
(async function initializeTheme(){const storedTheme=localStorage.getItem('theme');if(storedTheme){await setTheme(storedTheme);}else{setSystemTheme();}})();toggleTheme.onclick=async function(){const currentTheme=document.documentElement.getAttribute("data-theme");const targetTheme=currentTheme==="light"?"dark":"light";await setTheme(targetTheme);localStorage.setItem('theme',targetTheme);};toggleThemeMobile.onclick=toggleTheme.onclick;resetToSystemTheme.onclick=async function(){localStorage.removeItem('theme');setSystemTheme();};resetToSystemThemeMobile.onclick=resetToSystemTheme.onclick;window.matchMedia("(prefers-color-scheme: dark)").addEventListener("change",function(){if(!localStorage.getItem('theme')){setSystemTheme();}});